import{_ as g}from"./plugin-vue_export-helper-c27b6911.js";import{r as n,o as r,c as s,b as t,d as a,e as i,a as l}from"./app-c4cf960f.js";const o={},c=l('<h1 id="_36-张图梳理-intellij-idea-常用设置" tabindex="-1"><a class="header-anchor" href="#_36-张图梳理-intellij-idea-常用设置" aria-hidden="true">#</a> 36 张图梳理 Intellij IDEA 常用设置</h1><h2 id="_1-显示工具条" tabindex="-1"><a class="header-anchor" href="#_1-显示工具条" aria-hidden="true">#</a> <strong>1. 显示工具条</strong></h2><p>（1）效果图<br><img src="https://static.xlc520.tk/blogImage/IntellijIDEA1.png" alt="img" loading="lazy"><br> （2）设置方法</p><p>标注1：View–&gt;Toolbar</p><p>标注2：View–&gt;Tool Buttons</p><h2 id="_2-设置鼠标悬浮提示" tabindex="-1"><a class="header-anchor" href="#_2-设置鼠标悬浮提示" aria-hidden="true">#</a> <strong>2.设置鼠标悬浮提示</strong></h2><p>（1）效果图<br><img src="https://static.xlc520.tk/blogImage/IntellijIDEA2.png" alt="img" loading="lazy"></p><p>（2)设置方法</p><p><code>File–&gt;settings–&gt;Editor–&gt;General–&gt;勾选Show quick documentation… </code><br><img src="https://static.xlc520.tk/blogImage/IntellijIDEA3.png" alt="img" loading="lazy"></p><h2 id="_3-显示方法分隔符" tabindex="-1"><a class="header-anchor" href="#_3-显示方法分隔符" aria-hidden="true">#</a> <strong>3.显示方法分隔符</strong></h2><p>（1）效果图<br><img src="https://static.xlc520.tk/blogImage/IntellijIDEA4.png" alt="img" loading="lazy"><br> （2)设置方法</p><p><code>File–&gt;settings–&gt;Editor–&gt;Appearance–&gt;勾选 </code><br><img src="https://static.xlc520.tk/blogImage/IntellijIDEA5.png" alt="img" loading="lazy"></p><h2 id="_4-忽略大小写提示" tabindex="-1"><a class="header-anchor" href="#_4-忽略大小写提示" aria-hidden="true">#</a> <strong>4.忽略大小写提示</strong></h2><p>（1）效果图</p><p>备注：idea的默认设置是严格区分大小写提示的，例如输入string不会提示String，不方便编码<br><img src="https://static.xlc520.tk/blogImage/IntellijIDEA6.png" alt="img" loading="lazy"><br> （2）设置方法<br><code>File–&gt;settings–&gt;Editor–&gt;General --&gt;Code Completion --&gt; </code><br><img src="https://static.xlc520.tk/blogImage/IntellijIDEA7.png" alt="img" loading="lazy"></p><h2 id="_5-主题设置" tabindex="-1"><a class="header-anchor" href="#_5-主题设置" aria-hidden="true">#</a> <strong>5.主题设置</strong></h2><p>（1）效果图</p><p>备注：有黑白两种风格<br><img src="https://static.xlc520.tk/blogImage/IntellijIDEA8.png" alt="img" loading="lazy"><br><img src="https://static.xlc520.tk/blogImage/IntellijIDEA9.png" alt="img" loading="lazy"></p><p>（2)设置方法</p><p><code>File–&gt;settings–&gt;Appearance &amp; Behavior–&gt;Appearance–&gt; </code><br><img src="https://static.xlc520.tk/blogImage/IntellijIDEA10.png" alt="img" loading="lazy"></p><h2 id="_6-护眼主题设置" tabindex="-1"><a class="header-anchor" href="#_6-护眼主题设置" aria-hidden="true">#</a> <strong>6.护眼主题设置</strong></h2><p>（1）效果图<br><img src="https://static.xlc520.tk/blogImage/IntellijIDEA11.png" alt="img" loading="lazy"></p><p>（2)设置方法</p>',23),d=t("br",null,null,-1),h=t("img",{src:"https://static.xlc520.tk/blogImage/IntellijIDEA12.png",alt:"img",loading:"lazy"},null,-1),p=t("br",null,null,-1),m={href:"http://color-themes.com/?view=index&layout=Generic&order=popular&search=&page=1",target:"_blank",rel:"noopener noreferrer"},I=t("br",null,null,-1),b=t("img",{src:"https://static.xlc520.tk/blogImage/IntellijIDEA13.png",alt:"img",loading:"lazy"},null,-1),_=t("br",null,null,-1),x=t("br",null,null,-1),u=t("img",{src:"https://static.xlc520.tk/blogImage/IntellijIDEA14.png",alt:"img",loading:"lazy"},null,-1),E=t("br",null,null,-1),j=t("img",{src:"https://static.xlc520.tk/blogImage/IntellijIDEA15.png",alt:"img",loading:"lazy"},null,-1),f=t("br",null,null,-1),A=t("br",null,null,-1),k=t("img",{src:"https://static.xlc520.tk/blogImage/IntellijIDEA16.png",alt:"img",loading:"lazy"},null,-1),y=l('<h2 id="_7-自动导入包" tabindex="-1"><a class="header-anchor" href="#_7-自动导入包" aria-hidden="true">#</a> <strong>7. 自动导入包</strong></h2><p>(1）效果图 备注：默认情况是需要手动导入包的，比如我们需要导入Map类，那么需要手动导入，如果不需要使用了，删除了Map的实例，导入的包也需要手动删除，设置了这个功能这个就不需要手动了，自动帮你实现自动导入包和去包，不方便截图，效果请亲测~ （2）设置方法</p><p><code>File–&gt;settings–&gt;Editor–&gt;general–&gt;Auto Import–&gt; </code><br><img src="https://static.xlc520.tk/blogImage/IntellijIDEA17.png" alt="img" loading="lazy"></p><h2 id="_8-单行显示多个tabs" tabindex="-1"><a class="header-anchor" href="#_8-单行显示多个tabs" aria-hidden="true">#</a> <strong>8.单行显示多个Tabs</strong></h2><p>（1）效果图<br><img src="https://static.xlc520.tk/blogImage/IntellijIDEA18.png" alt="img" loading="lazy"></p><p>单行显示多个Tabs:<br><img src="https://static.xlc520.tk/blogImage/IntellijIDEA19.png" alt="img" loading="lazy"><br> （2）设置方法<br><code>File–&gt;settings–&gt;Editor–&gt;General --&gt;Editor Tabs–&gt;去掉√ </code><br><img src="https://static.xlc520.tk/blogImage/IntellijIDEA20.png" alt="img" loading="lazy"></p><h2 id="_9-设置字体" tabindex="-1"><a class="header-anchor" href="#_9-设置字体" aria-hidden="true">#</a> <strong>9.设置字体</strong></h2><p>（1）效果图 备注：默认安装启动Idea字体很小，看着不习惯，需要调整字体大小与字体（有需要可以调整）<br> （2）设置方法<br><code>File–&gt;settings–&gt;Editor–&gt;Font–&gt; </code><br><img src="https://static.xlc520.tk/blogImage/IntellijIDEA21.png" alt="img" loading="lazy"></p><h2 id="_10-配置类文档注释信息和方法注释模版" tabindex="-1"><a class="header-anchor" href="#_10-配置类文档注释信息和方法注释模版" aria-hidden="true">#</a> <strong>10.配置类文档注释信息和方法注释模版</strong></h2><p>（1）效果图 备注：团队开发时方便追究责任与管理查看<br><img src="https://static.xlc520.tk/blogImage/IntellijIDEA22.png" alt="img" loading="lazy"><br><img src="https://static.xlc520.tk/blogImage/IntellijIDEA23.jpg" alt="img" loading="lazy"></p>',10),D={href:"https://blog.csdn.net/zeal9s/article/details/83514565",target:"_blank",rel:"noopener noreferrer"},z=l('<h2 id="_11-水平或者垂直显示代码" tabindex="-1"><a class="header-anchor" href="#_11-水平或者垂直显示代码" aria-hidden="true">#</a> <strong>11.水平或者垂直显示代码</strong></h2><p>（1）效果图 备注：Eclipse如果需要对比代码，只需要拖动Tabs即可，但是idea要设置<br><img src="https://static.xlc520.tk/blogImage/IntellijIDEA24.png" alt="img" loading="lazy"></p><p>（2）设置方法</p><p>鼠标右击Tabs<br><img src="https://static.xlc520.tk/blogImage/IntellijIDEA25.png" alt="img" loading="lazy"></p><h2 id="_12-更换快捷键" tabindex="-1"><a class="header-anchor" href="#_12-更换快捷键" aria-hidden="true">#</a> <strong>12. 更换快捷键</strong></h2><p>（1）效果图</p><p>备注：从Eclipse移植到idea编码，好多快捷键不一致，导致编写效率降低，现在我们来更换一下快捷键</p><p>（2）设置方法</p><p>方法一： File–&gt;Setting–&gt; <img src="https://static.xlc520.tk/blogImage/IntellijIDEA26.png" alt="img" loading="lazy">例如设置成Eclipse的，设置好了之后可以ctrl+d删除单行代码（idea是ctrl+y）</p><p>方法二：设置模板 File–&gt;Setting–&gt; <img src="https://static.xlc520.tk/blogImage/IntellijIDEA27.png" alt="img" loading="lazy"></p><p>方法三： <img src="https://static.xlc520.tk/blogImage/IntellijIDEA28.png" alt="img" loading="lazy">以ctrl+o重写方法</p><p>为例 <img src="https://static.xlc520.tk/blogImage/IntellijIDEA29.png" alt="img" loading="lazy"></p><h2 id="_13-注释去掉斜体" tabindex="-1"><a class="header-anchor" href="#_13-注释去掉斜体" aria-hidden="true">#</a> <strong>13.注释去掉斜体</strong></h2><p>（1）效果图<br><img src="https://static.xlc520.tk/blogImage/IntellijIDEA30.png" alt="img" loading="lazy"></p><p>（2）设置方法</p><p>File–&gt;settings–&gt;Editor–&gt;<br><img src="https://static.xlc520.tk/blogImage/IntellijIDEA31.png" alt="img" loading="lazy"><br><img src="https://static.xlc520.tk/blogImage/IntellijIDEA32.png" alt="img" loading="lazy"></p><h2 id="_14-代码检测警告" tabindex="-1"><a class="header-anchor" href="#_14-代码检测警告" aria-hidden="true">#</a> <strong>14.代码检测警告</strong></h2><h2 id="提示等级设置" tabindex="-1"><a class="header-anchor" href="#提示等级设置" aria-hidden="true">#</a> <strong>提示等级设置</strong></h2><p><img src="https://static.xlc520.tk/blogImage/IntellijIDEA33.png" alt="img" loading="lazy"><br> 强烈建议，不要给关掉，不要嫌弃麻烦，他的提示都是对你好，帮助你提高你的代码质量，很有帮助的</p><h2 id="_15-项目目录相关–折叠空包" tabindex="-1"><a class="header-anchor" href="#_15-项目目录相关–折叠空包" aria-hidden="true">#</a> <strong>15.项目目录相关–折叠空包</strong></h2><figure><img src="https://static.xlc520.tk/blogImage/IntellijIDEA34.png" alt="img" tabindex="0" loading="lazy"><figcaption>img</figcaption></figure><h2 id="_16-窗口复位" tabindex="-1"><a class="header-anchor" href="#_16-窗口复位" aria-hidden="true">#</a> <strong>16. 窗口复位</strong></h2><p><img src="https://static.xlc520.tk/blogImage/IntellijIDEA35.png" alt="img" loading="lazy"><br> 这个就是当你把窗口忽然间搞得乱七八糟的时候，还可以挽回，就是直接restore一下，就好啦。</p><h2 id="_17-查看本地代码历史" tabindex="-1"><a class="header-anchor" href="#_17-查看本地代码历史" aria-hidden="true">#</a> <strong>17. 查看本地代码历史</strong></h2><figure><img src="https://static.xlc520.tk/blogImage/IntellijIDEA36.png" alt="img" tabindex="0" loading="lazy"><figcaption>img</figcaption></figure><h2 id="_18-快速补全分号" tabindex="-1"><a class="header-anchor" href="#_18-快速补全分号" aria-hidden="true">#</a> <strong>18. 快速补全分号</strong></h2><p>CTRL + SHIFT + ENTER 在当前行任何地方可以快速在末尾生成分号</p><h2 id="_19-快速找到controller方法" tabindex="-1"><a class="header-anchor" href="#_19-快速找到controller方法" aria-hidden="true">#</a> <strong>19. 快速找到Controller方法</strong></h2><p>如果你的项目里有非常多的controller，里面有非常多的http或者resful方法。如何快速找到这些方法呢？这个时候，<code>ctrl+alt+shift+n</code>就可以派上用场了。 比如说，你依稀记得入账单相关的接口，都有个bill的url路径，那么使用<code>ctrl+alt+shift+n</code>后，直接输入<code>/bill</code>即可。</p><p><img src="https://static.xlc520.tk/blogImage/IntellijIDEA37.jpg" alt="img" loading="lazy"><br> 当你在成千上万的Controller里寻找方法时，这一招就可以大大提高效率。</p><h2 id="_20-大括号匹配" tabindex="-1"><a class="header-anchor" href="#_20-大括号匹配" aria-hidden="true">#</a> <strong>20.大括号匹配</strong></h2><p>这个也非常有用，因为代码太长，某个for循环，可能已经撑满整个屏幕了。这个时候，找到某个大括号对应的另外一边就很费劲。你可以将光标定位在某个大括号一边，然后使用<code>ctrl+]</code>或者<code>ctrl+[</code>来回定位即可。 补充：以上的配置信息都保存在系统盘的<br><img src="https://static.xlc520.tk/blogImage/IntellijIDEA38.jpg" alt="img" loading="lazy"><br> 默认会有这两个文件<br><img src="https://static.xlc520.tk/blogImage/IntellijIDEA39.jpg" alt="img" loading="lazy"></p><p>config:在初始化安装IntelliJ IDEA时有询问你是否导入以存在的配置信息，这个config就是你的配置信息，方便更换电脑和换系统的时候重新安装，前提是要保存好此文件夹。 system:此文件夹是IntelliJ IDEA发生什么不可预知性的错误时，比如蓝屏，突然断电导致项目不能启动，可以尝试删除此文件，让系统重新生成一个system的文件</p>',33);function F(T,v){const e=n("ExternalLinkIcon");return r(),s("div",null,[c,t("p",null,[a("如果想将编辑页面变换主题，可以去设置里面调节背景颜色"),d,h,p,a(" 如果需要很好看的编码风格，这里有很多主题 "),t("a",m,[a("http://color-themes.com/?view=index&layout=Generic&order=popular&search=&page=1"),i(e)]),a(" 点击相应主题，往下滑点击按钮"),I,b,_,a(" 下载下来有很多Jar包"),x,u,E,j,f,a(" 在上面的位置选择导入jar包，然后重启idea生效，重启之后去设置"),A,k]),y,t("p",null,[a("（2）设置方法 "),t("a",D,[a("https://blog.csdn.net/zeal9s/article/details/83514565"),i(e)])]),z])}const V=g(o,[["render",F],["__file","36张图梳理 Intellij IDEA常用设置.html.vue"]]);export{V as default};
