import{d as p}from"./app.067ad56a.js";import{_ as t}from"./plugin-vue_export-helper.21dcd24c.js";const n={},g=p('<h1 id="\u4EBA\u751F\u6700\u597D\u7684\u4E09\u79CD\u72B6\u6001-\u4E0D\u671F\u800C\u9047-\u4E0D\u8A00\u800C\u55BB-\u4E0D\u836F\u800C\u6108" tabindex="-1"><a class="header-anchor" href="#\u4EBA\u751F\u6700\u597D\u7684\u4E09\u79CD\u72B6\u6001-\u4E0D\u671F\u800C\u9047-\u4E0D\u8A00\u800C\u55BB-\u4E0D\u836F\u800C\u6108" aria-hidden="true">#</a> \u4EBA\u751F\u6700\u597D\u7684\u4E09\u79CD\u72B6\u6001\uFF1A\u4E0D\u671F\u800C\u9047\uFF0C\u4E0D\u8A00\u800C\u55BB\uFF0C\u4E0D\u836F\u800C\u6108</h1><p>\u4EBA\u8FD9\u4E00\u8F88\u5B50\uFF0C\u4E0D\u8FC7\u77ED\u77ED\u6570\u5341\u8F7D\uFF0C\u6765\u53BB\u5306\u5306\u3002</p><p>\u5F88\u591A\u4EBA\u7EC8\u5176\u4E00\u751F\uFF0C\u90FD\u5728\u8FFD\u5BFB\u4E00\u79CD\u6700\u597D\u7684\u72B6\u6001\u3002</p><p>\u53EF\u7A76\u7ADF\u600E\u6837\u7684\u72B6\u6001\uFF0C\u624D\u7B97\u5F97\u4E0A\u6700\u597D\u7684\u72B6\u6001\u5462\uFF1F</p><p><strong>\u5176\u5B9E\uFF0C\u4EBA\u751F\u6700\u597D\u7684\u72B6\u6001\uFF0C\u5C31\u85CF\u5728\u8FD9\u4E09\u53E5\u8BDD\u91CC\u3002</strong></p><p><img src="https://nimg.ws.126.net/?url=http://dingyue.ws.126.net/2020/1227/adc9a8f1p00qlygon000td200u0007gg00hz004g.png&amp;thumbnail=660x2147483647&amp;quality=80&amp;type=jpg" alt="img" loading="lazy"></p><p>\u91CA\u5BB6\u8BF4\u7F18\u8D77\u7F18\u706D\uFF0C\u5F88\u591A\u4EBA\u6DF1\u4EE5\u4E3A\u7136\u3002\u6211\u4EEC\u4E00\u751F\u5927\u7EA6\u4F1A\u4E0E200\u4E07\u4EBA\u64E6\u80A9\u800C\u8FC7\uFF0C\u4E0E\u8BA4\u8BC6\u5927\u698220\u4E07\u4EBA\u3002\u4F5B\u8BF4\u6BCF\u4E00\u6B21\u7684\u64E6\u80A9\u800C\u8FC7\u90FD\u662F\u524D\u4E16\u7684\u4E00\u6B21\u56DE\u7738\uFF0C\u90A3\u4E0E\u4F60\u547D\u4E2D\u6CE8\u5B9A\u7684\u90A3\u4EBA\uFF0C\u8863\u670D\u90FD\u4F1A\u64E6\u70C2\u4E86\u5427\u3002\u7231\u60C5\u4E0D\u662F\u7528\u6765\u627E\u7684\uFF0C\u800C\u662F\u7B49\u7684\u3002\u5F88\u591A\u4EBA\u53BB\u5BFB\u627E\u7231\u60C5\uFF0C\u4F1A\u627E\u5230\uFF0C\u4F46\u662F\u8FC7\u7A0B\u5F88\u8270\u8F9B\u3002\u800C\u90A3\u79CD\u4E0D\u671F\u800C\u9047\u7684\u7231\u60C5\uFF0C\u9047\u89C1\u4E86\u5C31\u662F\u4E00\u8F88\u5B50\u3002\u4E5F\u8BB8\u5C31\u5728\u67D0\u5929\u6E05\u6668\uFF0C\u4F60\u63A8\u5F00\u7A97\u5B50\uFF0C\u697C\u4E0B\u6B63\u8D70\u7740\u7684\u4E00\u4F4D\u5973\u5B50\u521A\u597D\u62AC\u5934\u5411\u4E0A\u671B\u6765\uFF0C\u4F60\u4EEC\u56DB\u76EE\u76F8\u5BF9\uFF0C\u7F18\u5206\u5C31\u81EA\u7136\u6765\u4E86\u3002</p><p><strong>\u547D\u4E2D\u6CE8\u5B9A\u7684\u4EBA\uFF0C\u5373\u4F7F\u9694\u7740\u4E07\u6C34\u5343\u5C71\uFF0C\u4E5F\u603B\u4F1A\u6709\u4E00\u5929\uFF0C\u5728\u67D0\u4E2A\u5730\u70B9\u518D\u6B21\u91CD\u9022\u3002</strong></p><p>\u66FE\u770B\u8FC7\u4E00\u6BB5\u8BDD\uFF1A\u201C\u4EBA\u751F\uFF0C\u603B\u4F1A\u6709\u4E0D\u671F\u800C\u9047\u7684\u6E29\u6696\uFF0C\u548C\u751F\u751F\u4E0D\u606F\u7684\u5E0C\u671B\u3002\u201D</p><p>\u6240\u4EE5\uFF0C\u54EA\u6015\u56DB\u5B63\u66F4\u8FED\uFF0C\u6625\u53BB\u6625\u56DE\uFF0C\u6211\u4EEC\u4E5F\u603B\u4F1A\u5728\u67D0\u4E00\u4E2A\u8F6E\u56DE\u4E2D\uFF0C\u9047\u89C1\u90A3\u4E2A\u547D\u4E2D\u6CE8\u5B9A\u7684\u4EBA\u3002</p><p>\u7136\u540E\uFF0C\u6000\u62B1\u5E0C\u671B\uFF0C\u6E29\u6696\u4E00\u751F\u3002</p><p><img src="https://nimg.ws.126.net/?url=http%3A%2F%2Fdingyue.ws.126.net%2F2020%2F1227%2F84d63d90p00qlygon000vd200u0007gg00hz004g.png&amp;thumbnail=660x2147483647&amp;quality=80&amp;type=jpg" alt="img" loading="lazy"></p><p>\u771F\u6B63\u597D\u7684\u7231\u60C5\uFF0C\u5C31\u662F\u201C\u4E0D\u8D39\u529B\u201D\u3002\u4E0D\u9700\u8981\u53EF\u4EE5\u8BA8\u597D\u3001\u52AA\u529B\u7ECF\u8425\uFF0C\u4E24\u4E2A\u4EBA\u5DF2\u662F\u987A\u5176\u81EA\u7136\u7684\u8212\u670D\u3002\u5982\u679C\u4E00\u6BB5\u60C5\u3001\u4E00\u4E2A\u4EBA\uFF0C\u5F97\u8BA9\u4F60\u8017\u8D39\u5DE8\u5927\u7CBE\u529B\u6765\u53D6\u60A6\uFF0C\u8FD9\u5DF2\u6CE8\u5B9A\u4E0D\u662F\u966A\u4F60\u5230\u6700\u540E\u7684\u7F18\u5206\u4E86\u3002\u6700\u7231\u4F60\u7684\u4EBA\uFF0C\u4E0D\u4F1A\u820D\u5F97\u4F60\u5982\u6B64\u8F9B\u82E6\u3002\u4E24\u4E2A\u4EBA\u5373\u4F7F\u662F\u4E0D\u8BF4\u8BDD\uFF0C\u4E5F\u4E0D\u4F1A\u89C9\u5F97\u5C34\u5C2C\uFF0C\u5F88\u591A\u4E8B\u60C5\u90FD\u662F\u4E0D\u8A00\u800C\u55BB\u7684\uFF0C\u4EC5\u4EC5\u4E00\u4E2A\u773C\u795E\u3001\u4E00\u4E2A\u8868\u60C5\u5C31\u80FD\u660E\u767D\u5BF9\u65B9\u5FC3\u4E2D\u6240\u60F3\uFF0C\u8FD9\u624D\u662F\u7231\u60C5\u771F\u6B63\u7684\u6A21\u6837\u3002</p><p>\u4EBA\u8FD9\u4E00\u751F\uFF0C\u5927\u591A\u6570\u4EBA\u53EA\u662F\u4F60\u751F\u547D\u4E2D\u7684\u8FC7\u5BA2\uFF0C\u5306\u5306\u800C\u8FC7\uFF0C\u4E0D\u7559\u4E0B\u4EFB\u4F55\u75D5\u8FF9\uFF0C\u53EA\u6709\u5C11\u6570\u4EBA\u80FD\u771F\u6B63\u8D70\u8FDB\u4F60\u7684\u4E16\u754C\u3002</p><p>\u5728\u832B\u832B\u4EBA\u6D77\u4E2D\uFF0C\u82E5\u80FD\u9047\u5230\u4E00\u4E2A\u61C2\u4F60\u5FC3\u610F\uFF0C\u77E5\u4F60\u60B2\u6B22\u7684\u77E5\u5FC3\u4EBA\uFF0C\u4FBF\u662F\u4E07\u4E24\u9EC4\u91D1\u4E5F\u4E0D\u6362\u3002</p><p><strong>\u5728\u8FD9\u4E2A\u4EBA\u9762\u524D\uFF0C\u53EA\u8981\u4F60\u4E00\u4E2A\u773C\u795E\uFF0C\u5BF9\u65B9\u5C31\u80FD\u8BFB\u61C2\u4F60\u7684\u5FC3\u610F\uFF1B\u4E00\u4E2A\u52A8\u4F5C\uFF0C\u5BF9\u65B9\u5C31\u80FD\u660E\u767D\u4F60\u7684\u6240\u9700\u3002</strong></p><p>\u4E0D\u7528\u89E3\u91CA\u4EC0\u4E48\uFF0C\u4E5F\u4E0D\u7528\u523B\u610F\u4F2A\u88C5\u4EC0\u4E48\u3002</p><p>\u53EA\u6709\u771F\u6B63\u61C2\u4F60\u7684\u4EBA\uFF0C\u624D\u80FD\u8FBE\u5230\u8FD9\u79CD\u5FC3\u7075\u4E0A\u7684\u5951\u5408\uFF0C\u7CBE\u795E\u4E0A\u7684\u540C\u9891\u3002</p><p>\u5F88\u591A\u4E8B\u60C5\uFF0C\u5728\u4F60\u4EEC\u4E4B\u95F4\u65E9\u5C31\u4E0D\u8A00\u800C\u55BB\u3002</p><p><strong>\u5FC3\u82E5\u8FD1\u4E86\uFF0C\u4E00\u5207\u4FBF\u4E0D\u4F1A\u8FDC\u3002\u61C2\uFF0C\u6BD4\u4EC0\u4E48\u90FD\u91CD\u8981\u3002</strong></p><p><img src="https://nimg.ws.126.net/?url=http%3A%2F%2Fdingyue.ws.126.net%2F2020%2F1227%2F5dadd129p00qlygoo0010d200u0007gg00hz004g.png&amp;thumbnail=660x2147483647&amp;quality=80&amp;type=jpg" alt="img" loading="lazy"></p><p>\u6210\u5E74\u4EBA\u7684\u5B57\u5178\u91CC\u6CA1\u6709\u201C\u5BB9\u6613\u201D\u4E24\u4E2A\u4EBA\uFF0C\u6211\u4EEC\u4F1A\u7ECF\u5386\u79CD\u79CD\u56F0\u96BE\uFF0C\u604B\u4EBA\u79BB\u53BB\uFF0C\u5DE5\u4F5C\u4E0D\u987A\uFF0C\u5BB6\u4EBA\u751F\u75C5\u3002\u4EFB\u4F55\u4E00\u4EF6\u4E8B\u90FD\u8BA9\u6211\u4EEC\u60F3\u8981\u9003\u79BB\uFF0C\u4E0D\u60F3\u957F\u5927\u3002\u53EF\u662F\u6162\u6162\u5730\uFF0C\u6211\u4EEC\u518D\u6B21\u9762\u5BF9\u8FD9\u4E9B\u56F0\u96BE\u65F6\uFF0C\u6211\u4EEC\u80FD\u4ECE\u5BB9\u9762\u5BF9\u3002\u4E00\u5207\u78E8\u96BE\uFF0C\u4F60\u4E0D\u9700\u8981\u836F\u6765\u6CBB\u6108\uFF0C\u5C31\u662F\u4EBA\u751F\u7684\u6700\u597D\u72B6\u6001\u3002</p><p><strong>\u6210\u957F\u7684\u8DEF\u4E0A\uFF0C\u6211\u4EEC\u90FD\u4F1A\u9047\u5230\u5404\u79CD\u5404\u6837\u7684\u96BE\u5173\uFF0C\u90FD\u662F\u4E00\u8DEF\u5954\u8DD1\uFF0C\u4E00\u8DEF\u8DCC\u5012\uFF1B\u4E00\u8DEF\u5F97\u5230\uFF0C\u53C8\u4E00\u8DEF\u5931\u53BB\u3002</strong></p><p>\u6216\u8BB8\u5F88\u591A\u4EBA\uFF0C\u90FD\u66FE\u5728\u4EBA\u7FA4\u4E2D\uFF0C\u5047\u88C5\u5F00\u5FC3\uFF0C\u7B11\u5BB9\u6EE1\u9762\u3002\u5374\u5728\u56DB\u4E0B\u65E0\u4EBA\u65F6\uFF0C\u6495\u4E0B\u81EA\u5DF1\u770B\u4F3C\u575A\u5F3A\u7684\u5916\u8868\uFF0C\u9732\u51FA\u6700\u771F\u5B9E\u7684\u8106\u5F31\uFF0C\u63A9\u9762\u54ED\u6CE3\u3002</p><p>\u5728\u8FD9\u60B2\u559C\u4EA4\u9519\u7684\u4EBA\u751F\u4E2D\uFF0C\u6CA1\u6709\u4EBA\u662F\u4F60\u7684\u4E07\u80FD\u89E3\u836F\u3002</p><p>\u54EA\u6015\u662F\u65F6\u95F4\uFF0C\u4E5F\u4E0D\u53EF\u80FD\u6210\u4E3A\u4F60\u6700\u597D\u7684\u89E3\u836F\u3002</p><p>\u5C31\u50CF\u5B63\u7FA1\u6797\u5728\u4E66\u4E2D\u4F20\u8FBE\u7ED9\u6211\u4EEC\u7684\u4FE1\u5FF5\uFF1A\u6240\u6709\u7684\u60B2\u6B22\u79BB\u5408\uFF0C\u4ED6\u4EBA\u96BE\u609F\uFF0C\u60B2\u559C\u81EA\u6E21\u3002</p><p>\u6240\u4EE5\uFF0C\u4F60\u8981\u5B66\u4F1A\u72EC\u81EA\u53BB\u9762\u5BF9\u90A3\u4E9B\u4EBA\u751F\u96BE\u9898\uFF0C\u627E\u5230\u5C5E\u4E8E\u81EA\u5DF1\u7684\u7B54\u6848\u3002</p><p><strong>\u4F60\u8981\u505A\u5230\uFF0C\u5373\u4F7F\u6CA1\u6709\u89E3\u836F\uFF0C\u4E5F\u80FD\u591F\u81EA\u6211\u6CBB\u6108\u3002</strong></p>',29);function a(r,s){return g}var o=t(n,[["render",a],["__file","\u4EBA\u751F\u6700\u597D\u7684\u4E09\u79CD\u72B6\u6001.html.vue"]]);export{o as default};
